                <path d="M425.86,225.77a2,2,0,0,1,.77.72,2,2,0,0,1,.28,1,2.92,2.92,0,0,1-.31,1.29,6.16,6.16,0,0,1-.92,1.33c-.41.47-1,1.14-1.89,2h3.29L427,233h-4.32v-.81c1-1,1.67-1.76,2.08-2.24a5.92,5.92,0,0,0,.87-1.23,2.46,2.46,0,0,0,.27-1.14,1.14,1.14,0,0,0-1.21-1.22,1.64,1.64,0,0,0-.78.19,2.6,2.6,0,0,0-.72.62l-.66-.53a3,3,0,0,1,1-.84,2.7,2.7,0,0,1,1.22-.27A2.47,2.47,0,0,1,425.86,225.77Z" />
                <path d="M207.74,570.89a1.6,1.6,0,0,1,.67.67,1.89,1.89,0,0,1,.24,1,2.76,2.76,0,0,1-.26,1.2,6,6,0,0,1-.8,1.24c-.36.44-.91,1.06-1.65,1.85h2.87l-.11.79h-3.76v-.75c.85-1,1.45-1.64,1.81-2.08a6.23,6.23,0,0,0,.76-1.15,2.52,2.52,0,0,0,.23-1.06,1.16,1.16,0,0,0-.29-.83,1,1,0,0,0-.76-.3,1.34,1.34,0,0,0-.68.17,2.65,2.65,0,0,0-.63.57l-.57-.49a2.8,2.8,0,0,1,.86-.77,2.26,2.26,0,0,1,1.07-.26A1.9,1.9,0,0,1,207.74,570.89Z" />
